mapper.update:
update account
set account_status = 0
where account_id = #{accountId,jdbcType=BIGINT}

mapper.select:
SELECT account_id 
FROM   account 
WHERE  user_id = #{userId}

update.foreach:
UPDATE account
SET last_settlement_date = #{item.lastSettlementDate}
WHERE account_id = #{item.accountId}

mapper.select:
SELECT COUNT(account_id)
   FROM account
   WHERE account_id = #{accountId}
