mapper.update:
update account
set account_status = 0
where account_id = #{accountId,jdbcType=BIGINT}

mapper.select:
SELECT account_id 
FROM   account 
WHERE  user_id = #{userId}

update.foreach:
UPDATE account
SET last_settlement_date = #{item.lastSettlementDate}, update_date = #{item.updateDate}
WHERE account_id = #{item.accountId}

mapper.select:
SELECT COUNT(account_id)
   FROM account
   WHERE account_id = #{accountId}

mapper.update:
UPDATE account
SET account_status = #{accountStatus}, update_date = #{updateDate}
WHERE account_id = #{accountId}

mapper.select:
SELECT a.account_id AS accountId, a.provider_name AS providerName, a.responsible AS responsible, a.responsible_phone AS responsiblePhone, a.contract_end_date AS contractEndDate, a.regist_address_province AS registAddressProvince, r.name AS registAddressProvinceName, a.regist_address_city AS registAddressCity, (SELECT NAME FROM sys_region r WHERE a.regist_address_city = r.sr_id) AS registAddressCityName, a.regist_address_district AS registAddressDistrict, (SELECT NAME FROM sys_region r WHERE a.regist_address_district = r.sr_id) AS registAddressDistrictName, a.contract_register_address_province AS contractRegisterAddressProvince, (SELECT NAME FROM sys_region r WHERE a.contract_register_address_province = r.sr_id) AS contractRegisterAddressProvinceName, a.contract_register_address_city AS contractRegisterAddressCity, (SELECT NAME FROM sys_region r WHERE a.contract_register_address_city = r.sr_id) AS contractRegisterAddressCityName, a.contract_register_address_district AS contractRegisterAddressDistrict, (SELECT NAME FROM sys_region r WHERE a.contract_register_address_district = r.sr_id) AS contractRegisterAddressDistrictName FROM account a LEFT JOIN sys_region r ON a.regist_address_province = r.sr_id WHERE a.account_status = 1
AND a.provider_name LIKE '%${providerName}%'
AND a.account_id =#{accountId}

mapper.update:
UPDATE account
SET sms_monthago_type = 1
WHERE account_id = #{accountId}

