mapper.select:
select

from cart_item
where id = #{user_id,jdbcType=BIGINT}
AND id = #{supplier_id,jdbcType=BIGINT}
AND id = #{commodity_id,jdbcType=BIGINT}

mapper.insert:
insert into cart_item (id, user_id, supplier_id,
commodity_id, commodity_price, commodity_name,
commodity_properties, commodity_pic, supplier_name,
created_at, updated_at, count
)
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{supplierId,jdbcType=BIGINT},
#{commodityId,jdbcType=BIGINT}, #{commodityPrice,jdbcType=DECIMAL}, #{commodityName,jdbcType=VARCHAR},
#{commodityProperties,jdbcType=VARCHAR}, #{commodityPic,jdbcType=VARCHAR}, #{supplierName,jdbcType=VARCHAR},
#{createdAt,jdbcType=BIGINT}, #{updatedAt,jdbcType=BIGINT}, #{count,jdbcType=INTEGER}
)
