mapper.select:
SELECT
FROM supplier_commodity sc LEFT JOIN comm_measure_spec cms ON sc.measure_spec_id = cms.id LEFT JOIN comm_unit cu ON sc.unit_id=cu.id ,commodity c LEFT JOIN comm_brand cb ON c.brand_id=cb.id LEFT JOIN comm_category cc1 ON c.category_one_id=cc1.id LEFT JOIN comm_category cc2 ON c.category_two_id=cc2.id LEFT JOIN comm_category cc3 ON c.category_three_id=cc3.id WHERE sc.code69 = c.code69 AND sc.deleted = 0 AND sc.status = 2 AND sc.invalid_status = 1
AND sc.supplier_id = #{supplierId}
AND sc.sku = #{sku}
AND sc.code69 = #{code69}
AND sc.code = #{suppCommCode}
AND sc.price >= #{minPrice}
AND sc.price <= #{maxPrice}
AND sc.price BETWEEN #{minPrice} AND #{maxPrice}
AND ((cc1.name like '%${inputValue}%' OR cc2.name like '%${inputValue}%' OR cc3.name like '%${inputValue}%') OR c.name = #{inputValue})
ORDER BY sc.updated_at DESC

SELECT sc.id AS id, sc.min_img AS minImg, sc.sku AS sku, c.code69 AS code69, sc.code AS CODE, c.origin_place AS originPlace, cb.name AS brandName, c.name AS commName, cu.name AS unitName, cms.name AS measureSpecName, sc.rule_val AS ruleVal, sc.price AS price, sc.unit_price AS unitPrice, sc.inventory AS inventory, sc.created_at AS createdAt, sc.updated_at AS updatedAt, sc.invalid_status AS invalidStatus FROM supplier_commodity sc LEFT JOIN comm_measure_spec cms ON sc.measure_spec_id = cms.id LEFT JOIN comm_unit cu ON sc.unit_id=cu.id LEFT JOIN commodity c ON c.code69=sc.code69 LEFT JOIN comm_brand cb ON c.brand_id=cb.id LEFT JOIN comm_category cc1 ON c.category_one_id=cc1.id LEFT JOIN comm_category cc2 ON c.category_two_id=cc2.id LEFT JOIN comm_category cc3 ON c.category_three_id=cc3.id WHERE sc.invalid_status = 1 AND sc.deleted = 0 AND sc.status = 2
AND c.name LIKE '%${commName}%'
AND c.category_one_id = #{categoryOneId}
AND c.category_two_id = #{categoryTwoId}
AND c.category_three_id = #{categoryThreeId}
AND cb.id IN
#{id}
ORDER BY sc.updated_at DESC
