mapper.select:
SELECT sc.sku AS sku, sc.code69 AS code69, cb.name AS brandName, ct.name AS tagName, cc.name AS commName, sc.code AS supplierCode, cu.name AS unitName, cms.name AS measureSpecName, sc.rule_val AS ruleValue, sc.price AS price, sc.unit_price AS unitPrice, sc.inventory AS inventory, sc.status AS status, sc.created_at AS createdAt, sc.updated_at AS updatedAt, cc.company_name AS companyName, cc.origin_place AS originPlace, cc.market_time AS marketTime
FROM supplier_commodity sc INNER JOIN (commodity c LEFT JOIN comm_brand cb on c.brand_id = cb.id) on sc.code69 = c.code69 LEFT JOIN comm_measure_spec cms on sc.measure_spec_id = cms.id LEFT JOIN comm_unit cu on sc.unit_id = cu.id LEFT JOIN comm_tag ct on sc.tag_id = ct.id LEFT JOIN commodity cc on sc.code69 = cc.code69 WHERE sc.id IN
#{scId}
