mapper.update:
update freight_rules set
address_province = #{addressProvince,jdbcType=VARCHAR}, address_city = #{addressCity,jdbcType=VARCHAR}, address_district = #{addressDistrict,jdbcType=VARCHAR},
rules_type = #{rulesType,jdbcType=INTEGER}, whether_shipping = #{whetherShipping,jdbcType=INTEGER}, default_piece = #{defaultPiece,jdbcType=INTEGER}, default_amount = #{defaultAmount,jdbcType=DECIMAL}, excess_piece = #{excessPiece,jdbcType=INTEGER}, excess_amount = #{excessAmount,jdbcType=DECIMAL}, send_amount = #{sendAmount,jdbcType=DECIMAL},
remark = #{remark,jdbcType=VARCHAR}, update_at = #{updateAt,jdbcType=TIMESTAMP}
distribution_scope_id = #{distributionScopeId,jdbcType=INTEGER}
id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT id, supplier_id, rules_type, p.name address_province, c.name address_city, d.name address_district, whether_shipping, send_amount, default_piece, excess_piece, default_amount, excess_amount, remark, created_at, update_at,distribution_scope_id FROM freight_rules f LEFT JOIN sys_region p ON address_province = p.sr_id LEFT JOIN sys_region c ON address_city = c.sr_id LEFT JOIN sys_region d ON address_district = d.sr_id WHERE supplier_id = #{accountId,jdbcType=BIGINT}
AND rules_type = #{rulesType,jdbcType=INTEGER}
ORDER BY created_at DESC

SELECT id, supplier_id supplierId, rules_type rulesType, address_province AS addressProvince, address_city addressCity,address_district addressDistrict, p.name addressProvinceChinese, c.name addressCityChinese,d.name addressDistrictChinese, whether_shipping whetherShipping, send_amount sendAmount, default_piece defaultPiece, excess_piece excessPiece, default_amount defaultAmount, excess_amount excesAmount, remark, created_at createdAt, update_at updateAt,distribution_scope_id distributionScopeId FROM freight_rules f LEFT JOIN sys_region p ON address_province = p.sr_id LEFT JOIN sys_region c ON address_city = c.sr_id LEFT JOIN sys_region d ON address_district = d.sr_id WHERE supplier_id = #{accountId,jdbcType=BIGINT}
AND rules_type = #{rules,jdbcType=INTEGER}
ORDER BY created_at DESC
