mapper.select:
SELECT record_id, provider_name, checkout_at, total_money, settled_amount, settled_at, state, bank_account FROM order_money_record
AND checkout_at >= TRIM(STR_TO_DATE(#{startTime},'%Y-%m-%d'))
AND checkout_at < TRIM(STR_TO_DATE(#{endTime},'%Y-%m-%d'))
AND state = #{state}
limit ${limit}

SELECT SUM(settled_amount)
FROM order_money_record
WHERE user_id=#{accountId} AND state=1;

SELECT SUM(total_money)
FROM order_money_record
WHERE user_id=#{accountId} AND state=0;
