mapper.select:
SELECT order_id, order_receiver_name, order_receiver_mobile, order_status, order_price, order_create_time, order_payment_method, order_payment_num FROM purchase
WHERE order_id IN
#{orderId}

mapper.update:
UPDATE purchase
SET
order_status = #{orderStatus}
WHERE order_id = #{orderId}

UPDATE purchase


        SET drawback_time = #{drawbackTime}


        SET distributor_name = #{name},distributor_mobile = #{number},order_ship_method=1
        SET logistics_company = #{name} ,order_shipment_number = #{number},order_ship_method=2


WHERE order_id = #{orderId}

mapper.select:
SELECT SUM(order_price)
FROM purchase
WHERE store_id=#{userId} AND order_status=7 AND account_status=0;

mapper.update:
UPDATE purchase
SET account_status = 1
WHERE store_id=#{userId} AND order_status=7 AND account_status=0;

mapper.select:
SELECT COUNT(store_id)
FROM purchase
WHERE store_id = #{storeId,jdbcType=BIGINT}
