mapper.update:
UPDATE qualification qua
SET
qualification_status = #{qualificationStatus},
reason = #{ reason },
update_time = #{updateDate}
WHERE account_id = #{accountId};

mapper.select:
SELECT q.id AS id, a.account_id AS accountId, a.provider_name AS providerName, a.responsible_phone AS responsiblePhone, s.name AS province, s1.name AS city, s2.name AS district, q.create_time AS createTime, q.update_time AS updateTime, q.qualification_status AS qualificationStatus FROM qualification q LEFT JOIN account a ON q.account_id = a.account_id LEFT JOIN sys_region s ON a.regist_address_province = s.sr_id LEFT JOIN sys_region s1 ON a.regist_address_city = s1.sr_id LEFT JOIN sys_region s2 ON a.regist_address_district = s2.sr_id WHERE q.delete = 0
AND ( a.responsible LIKE CONCAT ('%',#{name},'%') OR a.provider_name LIKE CONCAT ('%',#{name},'%') )
AND a.qualification_status = #{status}
AND q.create_time >= #{startCreateTime}
AND q.create_time <= #{endCreateTime}
AND q.update_time >= #{startUpdateTime}
AND q.update_time <= #{endUpdateTime}
ORDER BY q.qualification_status,q.create_time
